public class Concatenation {

	
	
	void display()
	{
		String name1="Goo";
		String name2="gle";
		String name3=name1.concat(name2);
		System.out.println(name3);
	}
	
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		Concatenation s=new Concatenation();
		s.display();
		
	}

}
